//todo 的基础数据结构，用 mobx 管理
//数据驱动视图

import {  makeAutoObservable } from 'mobx'


class TaskStore {
  list = [
    {
      id:1,
      name: '学习react',
      isDone: false
    },
    {
      id:2,
      name: '搞定mobx',
      isDone: true
    },
    {
      id:3,
      name: '学习 ts',
      isDone: true
    }
  ]
  constructor() {
    makeAutoObservable(this)
  }

  //🔥🔥🔥checkbox 的单选操作，点击后就更改上面的数据
  changeCheckbox (id,check){
    //查找 id (在数组里边找到一个对象并进行修改就用 forEach，不会新增数组)
    this.list.forEach
  }

}
export default TaskStore
