function Task() {

  //ğŸ”¥ğŸ”¥ä» useStore è·Ÿå®ä¾‹ä¸­ã€è§£æ„èµ‹å€¼ã€‘å–å‡º taskStore è¿™ä¸ªå­å®ä¾‹ï¼
  const { taskStore } = useStore()


  //ğŸ‘‡ğŸ‘‡ğŸ‘‡è§†å›¾å±‚æ–¹æ³•(ä¼ å‚æ•°ç»™ storeï¼Œåœ¨ store é‚£é‡Œå»å¤„ç†æ•°æ®)
  //ğŸ”¥ğŸ”¥å®šä¹‰checkbox çš„æ–¹æ³•ï¼Œæ³¨æ„ï¼Œä¸æ˜¯åœ¨è¿™é‡Œç›´æ¥æ”¹æ•°æ®ï¼Œè€Œæ˜¯ä¼ ç»™ mobx =>  æ€è·¯ï¼šmobx Store å»ç»´æŠ¤çŠ¶æ€ï¼Œinput checkbox åªéœ€è¦æŠŠ e.target.value èµ‹å€¼ç»™ store å†…çš„æ–¹æ³•å³å¯ï¼
  //ğŸ”¥ğŸ”¥æ³¨æ„ï¼Œå› ä¸ºè¦ä¼ ä¸¤ä¸ªå‚æ•°ï¼Œä¼ ä¸äº†æ‰€ä»¥ä¸‹é¢çš„ input é‚£é‡Œæ¢æˆ ()=>XX() ç®­å¤´å‡½æ•°çš„å†™æ³•ï¼Œå…ˆä¼ ä¸€ä¸ªå‚æ•°ï¼Œè¿™é‡Œå†ä¼ å¤šä¸€ä¸ªï¼ï¼ï¼
  function onChange (id,e) {
    taskStore.changeCheckbox(id, e.target.checked)//ğŸ”¥ğŸ”¥ğŸ”¥ğŸ”¥ğŸ”¥æŠŠç‚¹å‡»åˆ°çš„ list çš„ id è·Ÿ checked çŠ¶æ€éƒ½ä¼ è¿›å»ï¼ï¼
    //è®¨å·§äº†ï¼ï¼ğŸ”¥ğŸ”¥å› ä¸ºåŸç”Ÿç³»ç»Ÿå®šä¹‰çš„ checked = true æ˜¯æœªæ‰“å‹¾çŠ¶æ€ğŸ”¥ğŸ”¥ï¼Œè€Œæˆ‘ä»¬çš„ isDone:false æ˜¯æœªæ‰“å‹¾ï¼Œæ‰€ä»¥ç›´æ¥ç›¸ç­‰å°±å¯ä»¥äº†ï¼Œä¸ç”¨ç‰¹åœ°å–è¿”ï¼ï¼
    console.log(e.target.checked)
  }


  //ğŸ”¥ğŸ”¥å®šä¹‰å…¨é€‰æ–¹æ³•ï¼ï¼ˆè§†å›¾å±‚ï¼‰
  function allChangeFn (e) {
    taskStore.allCheck(e.target.checked)
  }



  //ğŸ”¥ğŸ”¥å®šä¹‰åˆ é™¤æ–¹æ³•ï¼ˆè§†å›¾å±‚ï¼‰
  function delTask(id) {
    taskStore.deleteTask(id) //æŠŠ item å†…çš„ id ä¼ è¿›å», ç»™ store å†…å¤„ç†
  }



  //ğŸ”¥ğŸ”¥å®šä¹‰ã€å­˜å‚¨è¾“å…¥æ¡†å†…å®¹çš„æ•°ç»„ã€‘ï¼ˆåˆ©ç”¨ hook æ–¹æ³•ï¼‰ + ã€æ–°å¢ã€‘çš„æ–¹æ³• ï¼ˆè§†å›¾å±‚ï¼‰
  const [inputValue,setInputValue] = useState() //âš¡ï¸React å—æ§ç»„ä»¶çš„å¸¸è§„å†™æ³•ï¼ˆå®šä¹‰ã€ç»‘æ•°æ®ã€æ›´æ–°æ•°æ®ï¼‰
  function addTask () {
    taskStore.addTask( //âš¡ï¸âš¡ï¸âš¡ï¸ä¼ å…¥åŒæ ·æ•°æ®æ ¼å¼çš„å‚æ•°!!!
      {
        id:1,
        name: 'å­¦ä¹ react',
        isDone: false
      }
    )
  }


  //â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”â€”



  return (
    <section className="todoapp">
      <header className="header">
